            if (this._hashForUniforms[i].location == location)
                element = this._hashForUniforms[i];
        if (!element) {
            element = new cc.HashUniformEntry();
            element.location = location;
            element.value = data;
            this._hashForUniforms.push(element);
        } else {
            if (element.value == data)
                updated = false;
            else
                element.value = data;
        }
        return updated;
