	var closest_t = clamp01(vdot(seg_delta, vsub(center, seg_a))/vlengthsq(seg_delta));
	var closest = vadd(seg_a, vmult(seg_delta, closest_t));
	var contact = circle2circleQuery(center, closest, circleShape.r, segmentShape.r);
